import React, { useEffect, useRef } from 'react';
import { Carousel, CarouselItem, CarouselOptions, CarouselInterface, InstanceOptions } from 'flowbite';

const YourSliderComponent: React.FC = () => {
  const carouselRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const carouselElement = carouselRef.current;

    if (carouselElement) {
      const items: CarouselItem[] = [
        {
          position: 0,
          el: carouselElement.querySelector('#carousel-item-1'),
        },
        // ... (Repeat for other items)
      ];

      const options: CarouselOptions = {
        // Options here
      };

      const instanceOptions: InstanceOptions = {
        id: 'carousel-example',
        override: true,
      };

      const carousel: CarouselInterface = new Carousel(carouselElement, items, options, instanceOptions);

      carousel.cycle();

      const $prevButton = carouselElement.querySelector('#data-carousel-prev');
      const $nextButton = carouselElement.querySelector('#data-carousel-next');

      $prevButton.addEventListener('click', () => {
        carousel.prev();
      });

      $nextButton.addEventListener('click', () => {
        carousel.next();
      });
    }
  }, []);

  return (
    <div id="default-carousel" className="relative w-full" data-carousel="slide" ref={carouselRef}>
      {/* ... (Rest of the JSX code) ... */}
    </div>
  );
};

export default YourSliderComponent;
