// Cart.js
import React, { useState } from 'react';

const Cart = () => {
    const [cartItems, setCartItems] = useState([
        { id: 1, name: 'Product A', price: 20.0, quantity: 2 },
        { id: 2, name: 'Product B', price: 30.5, quantity: 1 },
    ]);

    const getTotalPrice = () => {
        return cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
    };

    const removeFromCart = (itemId) => {
        setCartItems(cartItems.filter(item => item.id !== itemId));
    };

    return (
        <div className='mx-2'>
            <h2>Your Cart</h2>
            <ul>
                {cartItems.map(item => (
                    <li key={item.id} className="flex items-center justify-between border-b border-gray-300 py-2">
                        <div>
                            <span className="font-semibold">{item.name}</span>
                            <span className="text-gray-600">Price: ${item.price} x {item.quantity}</span>
                        </div>
                        <button onClick={() => removeFromCart(item.id)} className="text-red-500 hover:underline">
                            Remove
                        </button>
                    </li>
                ))}
            </ul>
            <p className="text-lg font-semibold mt-4">Total: ${getTotalPrice()}</p>
        </div>
    );
};

export default Cart;
