// SelectCustom.js
import React from 'react';
import CreatableSelect from 'react-select/creatable';

const SelectCustom = (props) => {
  const customStyles = {
    control: (provided, state) => ({
      ...provided,
      color: 'your_custom_color', // Thay thế bằng màu chữ bạn muốn sử dụng
    }),
    // Thêm các custom styles khác nếu cần
  };
  const handleChange = (newValue, actionMeta) => {
    // Gọi hàm callback từ props và truyền giá trị mới
    if (props.onChange) {
      props.onChange(newValue);
    }
  };

  return <CreatableSelect isClearable options={props.options} styles={customStyles} onChange={handleChange} />;
};

export default SelectCustom;
