// src/components/common/Slider.js

import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const FeaturedSlider = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };

  return (
    <div className="w-full">
      <Slider {...settings}>
        <div className="w-full p-2">
          <img className="w-full h-auto" src="image1.jpg" alt="Image 1" />
        </div>
        <div className="w-full p-2">
          <img className="w-full h-auto" src="image2.jpg" alt="Image 2" />
        </div>
        {/* Add more slides as needed */}
      </Slider>
    </div>
  );
};

export default FeaturedSlider;
