            const fileInput = e.target;
            const files = fileInput.files;
            if (files.length > 0) {
                const uploadData = new FormData();
                uploadData.append("file", e.target.files[0], "file");
                const result = await uploads.uploadImg(uploadData);
                console.log(result);
                if (e.target.id == 'thumbnail')
