import React,{ useState } from "react";

const VoucherDropdown = ({ vouchers, onSelectVoucher }) => {
    const [isOpen, setIsOpen] = useState(false);
    const [selectedVoucher, setSelectedVoucher] = useState(null);
  
    const handleToggleDropdown = () => {
      setIsOpen(!isOpen);
    };
  
    const handleSelectVoucher = (voucher) => {
      setSelectedVoucher(voucher);
      onSelectVoucher(voucher); // Truyền voucher đã chọn ra ngoài
      setIsOpen(false);
    };
  
    return (
        <div className="relative inline-block">
        <button
          className="bg-blue-500 text-white p-2 rounded-md focus:outline-none"
          onClick={handleToggleDropdown}
        >
          {selectedVoucher ? selectedVoucher.name : 'Select Voucher'}
        </button>
  
        {isOpen && (
          <div className=" mt-2 bg-white border border-gray-300 rounded-md shadow-md p-4 ">
            {vouchers.map((voucher) => (
              <div
                key={voucher.id}
                className={`cursor-pointer mb-4 p-2 hover:bg-gray-100 rounded-md ${
                  selectedVoucher === voucher ? 'bg-gray-100' : ''
                }`}
                onClick={() => handleSelectVoucher(voucher)}
              >
                 <h3 className="text-lg font-semibold">{`Giảm ₫${voucher.value}k`}</h3>
              <p className="text-gray-600 mb-2">{`Đơn Tối Thiểu ₫${voucher.minOrderAmount}k`}</p>
              <p className="text-gray-600">{`HSD: ${voucher.expirationDate}`}</p>
              </div>
            ))}
          </div>
        )}
      </div>
  
    );
  };

  export default VoucherDropdown;
