// Dashboard.js
import React, { useState } from 'react';
import { Route, Switch, Link, Routes, NavLink } from 'react-router-dom';
import UserInfo from '../components/features/UserInfo';
import Orders from '../components/features/Orders';
import OrderDetail from '../components/features/OrderDetail';
import AccountManagement from '../components/features/AccountManagement';

const Dashboard = () => {

    return (
        <div className='max-w-screen-lg mx-auto mt-10 p-2 shadow rounded-md shadow-md'>
<div className="container-fluid">
      <div className="row">
        <div className="col-2 bg-dark min-height-100vh">
          <Sidebar />
        </div>
        <main className="col-10">
          <Dashboard />
        </main>
      </div>
    </div>
            <>
                <h2 className="my-3 text-white">Company name</h2>
                <div className="pt-3">
                    <ul className="nav nav-pills flex-column">
                        <li className="nav-item">
                            <NavLink className='nav-link' to='/dashboard/orders'>Orders</NavLink>
                        </li>

                        <li className="nav-item">
                            <NavLink className='nav-link' to='/dashboard/user-info'>user infor</NavLink>
                        </li>

                    </ul>
                </div>
            </>
            <Routes>
                <Route path="user-info" element={<UserInfo />} />
                <Route path="orders" element={<Orders />} />
                <Route path="order/:orderId" element={<OrderDetail />} />
                <Route path="/dashboard/account-management" element={<AccountManagement />} />
            </Routes>
        </div>


    );
};

export default Dashboard;
