import React, { useEffect, useState } from 'react';
import productService from '../services/productService';

const ProductDetail = ({ match }) => {
    const productId = match.params.id;

    // TODO: Fetch dữ liệu sản phẩm dựa trên productId từ API hoặc dữ liệu cứng

    // Dữ liệu sản phẩm (ví dụ)
    // const product1 = {
    //     id: productId,
    //     name: 'Product A',
    //     description: 'This is a sample product description.',

    //     price: 29.99,
    //     originalPrice: 39.99,
    //     discount: 10,
    //     image: 'https://example.com/product-image.jpg',
    // };
    const [product, setProduct] = useState({
        id: productId,
        name: 'Product A',
        description: 'This is a sample product description.',
        content: '',
        price: 29.99,
        originalPrice: 39.99,
        discount: 10,
        image: 'https://example.com/product-image.jpg',
    });

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await productService.getProduct();
                setProduct(data);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };

        fetchData();
    }, []);
    return (
        <div className="container mx-auto mt-10 p-6 bg-white rounded-md shadow-md">
            <div className="flex">
                <div className="w-1/2">
                    <img src={product.image} alt={product.name} className="rounded-lg" />
                </div>
                <div className="w-1/2 ml-6">
                    <h2 className="text-2xl font-semibold mb-4">{product.name}</h2>
                    <p className="text-gray-700 mb-4">{product.description}</p>
                    <div className="flex items-center mb-4">
                        <span className="text-lg font-semibold text-gray-800 mr-2">${product.price}</span>
                        {product.discount && (
                            <span className="text-sm text-gray-500 line-through">${product.originalPrice}</span>
                        )}
                    </div>
                    {product.discount && (
                        <p className="text-green-500 mb-4">{`Save $${product.discount} with discount!`}</p>
                    )}
                    <button
                        className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition-all duration-300"
                        onClick={() => alert('Product added to cart')}
                    >
                        Add to Cart
                    </button>
                </div>
            </div>
        </div>
    );
};

export default ProductDetail;
