// src/components/common/FeaturedSlider.js

import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const FeaturedSlider = () => {
  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };

  return (
    <div className="mt-8">
      <h2 className="text-2xl font-bold mb-4">Sản Phẩm Nổi Bật</h2>
      <Slider {...sliderSettings}>
        {/* Thêm các ảnh và thông tin sản phẩm vào đây */}
        <div className="px-2">
          <img src="https://gamikey.com/wp-content/uploads/2023/08/Banner-Kapersky.png.webp" alt="Product 1" />
          <p className="mt-2">Tên Sản Phẩm 1</p>
        </div>
        {/* ...Thêm các ảnh và thông tin sản phẩm khác */}
      </Slider>
    </div>
  );
};

export default FeaturedSlider;
