import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Checkout = ({ cartItems }) => {
  const [voucherCode, setVoucherCode] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('');

  const handleApplyVoucher = () => {
    // Xử lý logic áp dụng voucher, có thể gọi API để kiểm tra voucher code
    console.log('Applying voucher:', voucherCode);
  };

  const handlePaymentMethodChange = (method) => {
    // Xử lý thay đổi phương thức thanh toán
    setPaymentMethod(method);
  };

  const handleCheckout = () => {
    // Xử lý logic thanh toán, có thể gọi API để xác nhận đơn hàng
    console.log('Checking out with payment method:', paymentMethod);
  };

  const getTotalPrice = () => {
    const selectedItems = cartItems.filter(item => item.selected);
    const subtotal = selectedItems.reduce((total, item) => total + item.price * item.quantity, 0);
    return subtotal;
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded-md shadow-md">
      <h2 className="text-2xl font-semibold mb-6">Checkout</h2>

      {/* Section: Voucher */}
      <div className="mb-6">
        <label htmlFor="voucher" className="block text-gray-700 text-sm font-bold mb-2">
          Voucher Code
        </label>
        <div className="flex">
          <input
            type="text"
            id="voucher"
            className="flex-1 p-3 rounded-l-md border border-r-0 border-gray-300"
            placeholder="Enter your voucher code"
            value={voucherCode}
            onChange={(e) => setVoucherCode(e.target.value)}
          />
          <button
            className="bg-blue-500 text-white p-3 rounded-r-md hover:bg-blue-600 transition-all duration-300"
            onClick={handleApplyVoucher}
          >
            Apply
          </button>
        </div>
      </div>

      {/* Section: Payment Method */}
      <div className="mb-6">
        <label className="block text-gray-700 text-sm font-bold mb-2">Payment Method</label>
        <div className="flex">
          <label className="flex items-center cursor-pointer mr-4">
            <input
              type="radio"
              name="paymentMethod"
              value="creditCard"
              checked={paymentMethod === 'creditCard'}
              onChange={() => handlePaymentMethodChange('creditCard')}
            />
            <span className="ml-2">Credit Card</span>
          </label>
          <label className="flex items-center cursor-pointer">
            <input
              type="radio"
              name="paymentMethod"
              value="paypal"
              checked={paymentMethod === 'paypal'}
              onChange={() => handlePaymentMethodChange('paypal')}
            />
            <span className="ml-2">PayPal</span>
          </label>
        </div>
      </div>

      {/* Section: Order Summary */}
      <div className="mb-6">
        <h3 className="text-lg font-semibold mb-4">Order Summary</h3>
        <ul>
          {cartItems.map(item => (
            item.selected && (
              <li key={item.id} className="flex items-center justify-between border-b border-gray-300 py-2">
                <div className="flex items-center">
                  <img
                    src={item.image}
                    alt={item.name}
                    className="h-16 w-auto ml-4"
                  />
                  <div>
                    <span className="font-semibold">{item.name}</span>
                    <p className="text-gray-600">Price: ${item.price} x {item.quantity}</p>
                    <p className="text-gray-600">Category: {item.category}</p>
                  </div>
                </div>
              </li>
            )
          ))}
        </ul>
        <p className="text-lg font-semibold mt-4">Total: ${getTotalPrice().toFixed(2)}</p>
      </div>

      {/* Button: Checkout */}
      <button
        className="w-full bg-blue-500 text-white p-3 rounded-md hover:bg-blue-600 transition-all duration-300"
        onClick={handleCheckout}
      >
        Checkout
      </button>
    </div>
  );
};

export default Checkout;
