import React, { useEffect, useState } from 'react';
import productService from '../services/productService';
import { useParams } from 'react-router-dom';

const ProductDetail = () => {
  const { id } = useParams();

  const [product, setProduct] = useState({
    id,
    name: 'Product A',
    description: 'This is a sample product description.',
    content: '',
    price: 29.99,
    originalPrice: 39.99,
    discount: 10,
    image: 'https://example.com/product-image.jpg',
    thumbnail: '',
    published: false,
    ProductCategoryId: 1,
    ProductTypeId: 1,
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await productService.getProduct(id);
        setProduct(data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchData();
  }, [id]);

  return (
    <div className="container mx-auto mt-10 p-6 bg-white rounded-md shadow-md">
      <div className="flex">
        <div className="w-1/2">
          <img src={product.image} alt={product.name} className="rounded-lg" />
        </div>
        <div className="w-1/2 ml-6">
          <h2 className="text-2xl font-semibold mb-4">{product.name}</h2>
          <div className="grid grid-cols-2 gap-4">
            <div>
              <p className="font-semibold">Description:</p>
              <p className="text-gray-700 mb-4">{product.description}</p>
            </div>
            <div>
              <p className="font-semibold">Price:</p>
              <p className="text-lg font-semibold text-gray-800">${product.price}</p>
              {product.discount && (
                <p className="text-sm text-gray-500 line-through">${product.originalPrice}</p>
              )}
              {product.discount && (
                <p className="text-green-500">{`Save $${product.discount} with discount!`}</p>
              )}
            </div>
          </div>
          <button
            className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition-all duration-300 mt-4"
            onClick={() => alert('Product added to cart')}
          >
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
