import React from 'react';

const ProductDetail = ({ product }) => {
  return (
    <div className="container mx-auto mt-10 p-6 bg-white rounded-md shadow-md">
      <div className="flex">
        <div className="w-1/2">
          <img src={product.image} alt={product.name} className="rounded-lg" />
        </div>
        <div className="w-1/2 ml-6">
          <h2 className="text-2xl font-semibold mb-4">{product.name}</h2>
          <p className="text-gray-700 mb-4">{product.description}</p>
          <div className="flex items-center mb-4">
            <span className="text-lg font-semibold text-gray-800 mr-2">${product.price}</span>
            {product.discount && (
              <span className="text-sm text-gray-500 line-through">${product.originalPrice}</span>
            )}
          </div>
          {product.discount && (
            <p className="text-green-500 mb-4">{`Save $${product.discount} with discount!`}</p>
          )}
          <button
            className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition-all duration-300"
            onClick={() => alert('Product added to cart')}
          >
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
