import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../assets/styles/Cart.css';
import cartService from '../services/cartService';
import voucherService from '../services/voucherService';
import Modal from 'react-modal';

Modal.setAppElement('#root');

const Cart = () => {
    const VND = new Intl.NumberFormat('vi-VN', {
        style: 'currency',
        currency: 'VND',
    });

    const [cartItems, setCartItems] = useState([]);
    const [voucherText, setVoucherText] = useState('Chọn Voucher');
    const [totalText, setTotalText] = useState('0');
    const [isModalOpen, setModalOpen] = useState(false);
    const [selectedVoucher, setSelectedVoucher] = useState(null);
    const [voucher, setVoucher] = useState([]);
    const [voucherCode, setVoucherCode] = useState('');
    const [checkVoucher, setCheckVoucher] = useState([]);
    const [discount, setDiscount] = useState(0);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await cartService.getCartByUser(1);
                setCartItems(data);

                const datavoucher = await voucherService.getvoucherbyproductcategory({ product: data });
                setVoucher(datavoucher);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };

        fetchData();
    }, []);
    useEffect(() => {
        if (selectedVoucher !== null) {
            const text = selectedVoucher.discount_percentage === 0
                ? `Giảm ${selectedVoucher.discount_amount}đ`
                : `Giảm ${selectedVoucher.discount_percentage}%`;

            setVoucherText(text);
        } else {
            setVoucherText('Chọn Voucher');
        }
    }, [selectedVoucher]);
    // useEffect(() => {
    //     if (checkVoucher !== null) {
    //         const text = selectedVoucher.discount_percentage === 0
    //             ? `Giảm ${selectedVoucher.discount_amount}đ`
    //             : `Giảm ${selectedVoucher.discount_percentage}%`;

    //         setTotalText(text);
    //     } else {
    //         setto('Chọn Voucher');
    //     }
    // }, [selectedVoucher]);

    const openModal = () => setModalOpen(true);
    const closeModal = () => setModalOpen(false);
    const handleSelectVoucher = async (voucher) => {
        const selectedItems = cartItems.filter(item => item.selected);

        if (selectedItems.length >= 1) {
            setSelectedVoucher(voucher);
            const data = await cartService.checkVoucher({ UserId: 1, Items: selectedItems, VoucherCode: voucher?.code });
            setCheckVoucher(data);
            closeModal();
            return;
        }


        closeModal();
    };

    const getTotalPrice = () => {
        const selectedItems = cartItems.filter(item => item.selected);
        const subtotal = selectedItems.reduce((total, item) => total + (item?.Subscription_plan?.price - (item?.Subscription_plan?.price * item?.Subscription_plan?.discount_percentage / 100)) * item.quantity, 0);
        return subtotal - (subtotal * (discount / 100));
    };

    const removeFromCart = (itemId) => {
        setCartItems(cartItems.filter(item => item.id !== itemId));
    };

    const toggleSelect = (itemId) => {
        setCartItems(cartItems.map(item => (item.id === itemId ? { ...item, selected: !item.selected } : item)));
        checkVoucher.totalPayment = null;
        checkVoucher.totalDiscount = null;
        setSelectedVoucher(null);

    };

    const applyVoucher = () => {
        setDiscount(10); // 10% discount for demonstration purposes
    };
    const handleButtonClickQuantity = async (item,value) => {
        try {
            const data = await cartService.getCartByUser(1);
            setCartItems(data);

            const datavoucher = await voucherService.getvoucherbyproductcategory({ product: data });
            setVoucher(datavoucher);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
        // if (planChoose > 0) {
        //   const foundProduct = product.subscriptionPlans.find(item => item.id === planChoose);
    
        //   if (foundProduct.total - foundProduct.quantity_sold < 0 || quantity + value > foundProduct.total - foundProduct.quantity_sold) {
        //      showMessage('Vượt quá số lượng');
        //      return;
        //   };
        //   if (quantity + value < 1) return;
        //   setQuantity(quantity + value);
    
        // }
      }
    

    return (
        <div className='max-w-screen-lg mx-auto mt-10 p-6 shadow rounded-md shadow-md'>
            <h2>Your Cart</h2>
            <ul>
                {cartItems.map(item => (
                    <li key={item.id} className="grid grid-cols-4 gap-4 border-b border-gray-300 py-2">
                        <div className="flex items-center">
                            <label className="checkbox-container mx-2">
                                <input
                                    type="checkbox"
                                    checked={item.selected || false}
                                    value={false}
                                    onChange={() => toggleSelect(item.id)}
                                />
                                <span className="checkmark"></span>
                            </label>
                            <img
                                src={item?.Subscription_plan?.Product?.image}
                                alt={item?.Subscription_plan?.Product?.image}
                                className="h-14 w-auto ml-4 mx-2"
                            />
                            <div>
                                <span className="font-semibold text-base">{item?.Subscription_plan?.Product?.name}</span>
                                <p className="text-gray-600">Price:  {VND.format(item?.Subscription_plan?.price - (item?.Subscription_plan?.price * item?.Subscription_plan?.discount_percentage / 100))} x {item?.quantity}</p>
                                <p className="text-gray-600"> {item?.Subscription_plan?.packed_name}</p>
                            </div>
                        </div>
                        
                        <div>
                        </div>
                        <div class="p-2 border-2 border-gray-600 mx-1 rounded-md my-2 w-2/3" data-hs-input-number>
                            <div class="w-full flex justify-between items-center gap-x-5">
                                <div class="grow">

                                    <input class="w-full p-2 bg-transparent border-0 text-gray-800 focus:ring-0 " type="text" value={item?.quantity} data-hs-input-number-input />
                                </div>
                                <div class="flex justify-end items-center gap-x-1.5">
                                    <button onClick={() => handleButtonClickQuantity(item,-1)} type="button" class="w-6 h-6 inline-flex justify-center items-center gap-x-2 text-sm font-medium rounded-full border border-gray-200 bg-white text-gray-800 shadow-sm hover:bg-gray-50 disabled:opacity-50 disabled:pointer-events-none dark:bg-slate-900 dark:border-gray-700 dark:text-white dark:hover:bg-gray-800 dark:focus:outline-none dark:focus:ring-1 dark:focus:ring-gray-600" data-hs-input-number-decrement>
                                        <svg class="flex-shrink-0 w-3.5 h-3.5" xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><path d="M5 12h14" /></svg>
                                    </button>
                                    <button onClick={() => handleButtonClickQuantity(item,1)} type="button" class="w-6 h-6 inline-flex justify-center items-center gap-x-2 text-sm font-medium rounded-full border border-gray-200 bg-white text-gray-800 shadow-sm hover:bg-gray-50 disabled:opacity-50 disabled:pointer-events-none dark:bg-slate-900 dark:border-gray-700 dark:text-white dark:hover:bg-gray-800 dark:focus:outline-none dark:focus:ring-1 dark:focus:ring-gray-600" data-hs-input-number-increment>
                                        <svg class="flex-shrink-0 w-3.5 h-3.5" xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><path d="M5 12h14" /><path d="M12 5v14" /></svg>
                                    </button>
                                </div>
                            </div>
                            
                        </div>
                        <button onClick={() => removeFromCart(item.id)} className="text-red-500 hover:underline text-base">
                            Remove
                        </button>
                    </li>
                ))}
            </ul>
            <div className="mb-6">
                <label className="block text-gray-700 text-sm font-bold mb-2">Voucher Code</label>
                <button
                    className="bg-orange-500 text-white p-2 rounded-md focus:outline-none"
                    onClick={openModal}
                >
                    {voucherText}
                </button>
                {
                    selectedVoucher ? <button className='mx-3 p-2 shadow rounded-md' onClick={() => setSelectedVoucher(null)}>Cancel  </button> : null
                }

                <Modal
                    isOpen={isModalOpen}
                    onRequestClose={closeModal}
                    contentLabel="Example Modal"
                    style={{
                        content: {
                            width: '50%', // Thiết lập chiều rộng
                            height: '50%', // Thiết lập chiều cao
                            margin: 'auto',
                        },
                    }}
                >
                    <div className='flex justify-between p-2'>
                        <h2>Voucher</h2>
                        <p>Chọn Voucher</p>
                        <button className='bg-orange-400 p-2 rounded-md ' onClick={closeModal}>Close</button>
                    </div>
                    <div className="max-h-screen overflow-y-scroll p-4">
                        <h2 className="text-xl font-semibold mb-4">Chọn Voucher</h2>
                        <div className="voucher-container">
                            {voucher?.map(item => (
                                <div
                                    key={item.code}
                                    className="cursor-pointer mb-4 p-2 hover:bg-gray-100 rounded-md voucher-item"
                                    onClick={() => handleSelectVoucher(item)}
                                >
                                    <h3 className="text-lg font-semibold">{item?.discount_percentage == 0 ? (`Giảm ${item.discount_amount}đ`) : (`Giảm ${item.discount_percentage}%`)}</h3>
                                    <p className="text-gray-600 mb-2">{`Đơn Tối Thiểu ₫${item.min_order_amount}k`}</p>
                                    <p className="text-gray-600">{`HSD: ${new Date(item.end_date).toLocaleDateString()} - ${new Date(item.end_date).toLocaleTimeString()}`}</p>
                                </div>
                            ))}
                        </div>
                    </div>
                </Modal>
            </div>
            <div className="mt-4">
                <label className="block text-gray-600">Voucher Code:</label>
                <input
                    type="text"
                    placeholder="Enter voucher code"
                    className="border-2 border-gray-300 p-2 rounded-md focus:outline-none focus:border-orange-500"
                // value={voucherCode}
                // onChange={(e) => setVoucher(e.target.value)}
                />
                <button onClick={applyVoucher} className="bg-orange-500 text-white px-4 py-2 rounded-md ml-2 hover:bg-orange-600">
                    Apply Voucher
                </button>
            </div>
            <p className="text-lg font-semibold mt-4">total: {VND.format(getTotalPrice().toFixed(2))}</p>
            <p className="text-lg font-semibold mt-4">totalDiscount: {VND.format(checkVoucher?.totalDiscount?.toFixed(2) || 0)}</p>
            <p className="text-lg font-semibold mt-4">totalPayment: {VND.format(checkVoucher?.totalPayment?.toFixed(2) || getTotalPrice().toFixed(2))}</p>


            {/* Nút Mua Hàng */}
            <Link to="/checkout">
                <button className=" bg-orange-500 text-white px-4 py-2 rounded-md mt-4 hover:bg-green-600">
                    Checkout
                </button>
            </Link>

            {/* Hiển thị số lượng sản phẩm trong giỏ hàng */}
            <p className="text-gray-600 mt-2">Items in Cart: {cartItems.length}</p>
        </div>
    );
};

export default Cart;
