// LoginForm.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import authService from '../services/auth';
import { ToastContainer, toast } from 'react-toastify';
import { useUser } from '../context/userProvider';
import { Navigate } from 'react-router-dom'
const showMessage = (message) => {
  toast.success(message, {
    position: 'top-right',
    autoClose: 3000, // Đóng tự động sau 3000 milliseconds (3 giây)
    hideProgressBar: false,
    closeOnClick: true,
    pauseOnHover: true,
    draggable: true,
  });
}

const LoginForm = () => {
  const navigate = useNavigate();
  const { user, login, logout } = useUser();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  useEffect(() => {
    // Kiểm tra xem người dùng đã đăng nhập chưa
    if (user) {
      // Nếu đã đăng nhập, chuyển hướng tới trang chủ hoặc trang khác theo nhu cầu của bạn
      navigate('/');
    }
  }, [user, navigate]);

  const handleLogin = async () => {
    // Logic for handling login
    // console.log('Logging in with:', email, password);
    try {

      const data = await authService.login({ username: email, password: password });
      window.localStorage.setItem("accessToken", data.accessToken);
      showMessage("Đăng nhập thành công");
      
      const payloadBase64  = data.accessToken.split(".")[1];
      const payload = atob(payloadBase64 );
      login(JSON.parse(payload));
      
    } catch (error) {
      showMessage("Đăng nhập thất bại");
      console.log(error)
    }

  };

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded-md shadow-md">
      <ToastContainer />
      <h2 className="text-2xl font-semibold mb-6">Login</h2>
      <div className="mb-4">
        <label htmlFor="email" className="block text-gray-700 text-sm font-bold mb-2">
          Email Address
        </label>
        <input
          type="email"
          id="email"
          className="w-full p-3 rounded-md border border-gray-300"
          placeholder="Enter your email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </div>
      <div className="mb-6">
        <label htmlFor="password" className="block text-gray-700 text-sm font-bold mb-2">
          Password
        </label>
        <input
          type="password"
          id="password"
          className="w-full p-3 rounded-md border border-gray-300"
          placeholder="Enter your password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>
      <Link to="/forgot-password" className="text-blue-500 hover:underline ">
        Forgot Password?
      </Link>
      <button
        className="w-full bg-blue-500 text-white p-3 rounded-md hover:bg-blue-600 transition-all duration-300"
        onClick={handleLogin}
      >
        Login
      </button>
    </div>
  );
};

export default LoginForm;
