// Dashboard.js
import React from 'react';
import { Link, Route, Routes } from 'react-router-dom';
import UserInfo from './UserInfo';
import Orders from './Orders';
import AccountManagement from './AccountManagement';
// import DefaultView from './DefaultView'; // Thêm import cho DefaultView
import './Dashboard.css';

const Dashboard = () => {
  return (
    <div className="container">
      <h1>Dashboard</h1>
      <nav>
        <ul>
          <li><Link to="/dashboard/user-info">User Info</Link></li>
          <li><Link to="/dashboard/orders">Orders</Link></li>
          <li><Link to="/dashboard/account-management">Account Management</Link></li>
          <li><Link to="/logout" className="logout-btn">Logout</Link></li>
        </ul>
      </nav>
      <div className="dashboard-content">
        <Routes>
          <Route path="/" element={<UserInfo />} /> {/* Component mặc định */}
          <Route path="/dashboard/user-info" element={<UserInfo />} />
          <Route path="/dashboard/orders" element={<Orders />} />
          <Route path="/dashboard/account-management" element={<AccountManagement />} />
        </Routes>
      </div>
    </div>
  );
};

export default Dashboard;
