import React, { useState } from 'react';

const Header = () => {
  const [isMenuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!isMenuOpen);
  };

  return (
    <header className="bg-gray-800 text-white p-4 flex justify-between items-center">
      {/* Biểu tượng menu cho điện thoại */}
      <div className="lg:hidden">
        <button onClick={toggleMenu}>
          <svg
            className="w-6 h-6 text-white"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M4 6h16M4 12h16m-7 6h7"
            />
          </svg>
        </button>
      </div>

      {/* Menu */}
      <nav
        className={`lg:hidden absolute inset-x-0 top-full transition-transform transform ${isMenuOpen ? 'translate-y-0' : '-translate-y-0'
          } bg-gray-800 text-white p-4 z-10`}
      >
        <ul className="flex flex-col lg:flex-row space-y-4 lg:space-y-0">
          <li><a href="#" className="hover:text-gray-300">Trang Chủ</a></li>
          <li><a href="#" className="hover:text-gray-300">Danh Mục Sản Phẩm</a></li>
          <li><a href="#" className="hover:text-gray-300">Giới Thiệu</a></li>
          <li><a href="#" className="hover:text-gray-300">Liên Hệ</a></li>
        </ul>
      </nav>

      {/* Tìm kiếm */}
      <div className="search-bar flex items-center space-x-2">
        <input type="text" placeholder="Tìm kiếm sản phẩm..." className="p-2 border border-gray-500" />
        <button type="button" className="p-2 bg-blue-500 text-white">Tìm Kiếm</button>
      </div>

      {/* Đăng nhập và Đăng kí */}
      <div className="auth-section space-x-4">
        <a href="#" className="hover:text-gray-300">Đăng Nhập</a>
        <a href="#" className="hover:text-gray-300">Đăng Kí</a>
      </div>
    </header>
  );
};

export default Header;
