// src/components/Header.js
import React, { useState } from 'react';
import logo from '../../assets/images/logo.png';
import { Link } from 'react-router-dom';
const Header = () => {
  const [isListVisible, setListVisible] = useState(false);

  const toggleListVisibility = () => {
    setListVisible(!isListVisible);
  };
  return (
    <header className="bg-gray-800 p-4">
      <div className="container mx-auto flex justify-around items-center">
        {/* Logo hoặc Tên ứng dụng */}
        <div className="flex items-center">
          {/* <img
            src={logo}  // Thay đổi đường dẫn đến logo của bạn
            alt="Logo"
            className="h-12 w-auto mr-2"  // Điều chỉnh chiều cao và tự động điều chỉnh chiều rộng
          /> */}
          <h1 className="text-white text-2xl font-bold">AccTradeRent</h1>
          <Link to="/login" className="text-white hover:text-gray-300">
            Login
          </Link>
        </div>

        {/* Navigation Menu */}


        {/* Tìm kiếm */}
        <div className="flex items-center space-x-4 ">
          <nav className="space-x-4">

            <button
              onClick={toggleListVisibility}
              className="bg-orange-500 text-white px-4 py-2 rounded-md"
            >
              Danh Mục
            </button>
            {isListVisible && (
              <div className="absolute top-20 right-1/2 transform translate-x-1/2 bg-gray-800 text-white border border-gray-300 p-6 rounded-md z-20 w-screen md:w-4/5">
                {/* Danh sách loại sản phẩm */}
                <ul>
                  <li>Loại sản phẩm 1</li>
                  <li>Loại sản phẩm 2</li>
                  <li>Loại sản phẩm 3</li>
                  {/* Thêm các mục loại sản phẩm khác tùy ý */}
                </ul>
              </div>
            )}
          </nav>
          <input
            type="text"
            placeholder="Search..."
            className="border-2 border-gray-300 p-2 rounded-md focus:outline-none focus:border-blue-500 w-100"
          />
          <button className="absolute right-2 top-1/2 transform -translate-y-1/2">
            {/* Thêm biểu tượng tìm kiếm ở đây (ví dụ: Font Awesome) */}
            <i className="fas fa-search text-white"></i>
          </button>
        </div>

        {/* Giỏ hàng */}
        <div className="flex items-center space-x-4">
          {/* Biểu tượng giỏ hàng hoặc liên kết đến trang giỏ hàng */}
          <a href="#" className="text-white hover:text-gray-300">
            <i className="fas fa-shopping-cart text-2xl"></i>
          </a>

          {/* Đăng nhập và Đăng ký */}
          {/* <a href="#" className="text-white hover:text-gray-300">Log In</a> */}
          <Link to="/login" className="text-white hover:text-gray-300">
            Login
          </Link>
          <Link to="/register" className="bg-orange-500 text-white-500 px-4 py-2 rounded-md hover:bg-gray-100">
          Sign Up
          </Link>
          
        </div>
      </div>
    </header>
  );
};

export default Header;
