// src/components/CMS.js
import React from 'react';
import RoleManager from '../components/features/RoleManager';
import ProductManagement from '../components/features/ProductManagement';
import UserManagement from '../components/features/UserManagement';
import SidebarCMS from '../components/common/SiderbarCMS';
import { NavLink, Navigate, Route, Routes } from 'react-router-dom';
import AccountManagement from '../components/features/AccountManagement'
import VoucherManagement from '../components/features/VoucherManagement'
const CMS = () => {
    return (
        <div >

            {/* Sidebar */}
            <div className='flex mx-auto-20 mt-10 bg-white rounded-md shadow-md'>

                <div className="bg-gray-800 text-white h-1/2 flex flex-col rounded-md shadow-md">


                    <nav className="flex-1">
                        <div className="p-2 grid grid-rows-3 grid-flow-col gap-4">
                            {/* <div className="row-span-3 ...">
            <img src="https://down-vn.img.susercontent.com/file/vn-11134233-7r98o-lmnn9g0zf3dre3_tn" alt=""  className='rounded-lg'/>
        </div> */}
                            {/* <div className="col-span-2 ...">{user?.username}</div> */}

                        </div>
                        <ul className="space-y-2 mx-2 my-2">

                            <li>
                                <NavLink to="/cms/user-manager" className="block p-2 hover:bg-gray-700">
                                    Người Dùng
                                </NavLink>
                            </li>
                            <li>
                                <NavLink to="/cms/product-manager" className="block p-2 hover:bg-gray-700">
                                    Sản phẩm
                                </NavLink>
                            </li>
                            <li>
                                <NavLink to="/cms/voucher-manager" className="block p-2 hover:bg-gray-700">
                                    Voucher
                                </NavLink>
                            </li>

                            <li>
                                <NavLink to="/cms/account-manager" className="block p-2 hover:bg-gray-700">
                                    Tài khoản
                                </NavLink>
                            </li>
                            <li>
                                <NavLink to="/cms/orders" className="block p-2 hover:bg-gray-700">
                                    Giao dịch
                                </NavLink>
                            </li>
                            <li>
                                <NavLink to="/cms/orders" className="block p-2 hover:bg-gray-700">
                                    Phương thức thanh toán
                                </NavLink>
                            </li>
                            {/* Add more NavLink items for additional links */}
                        </ul>
                    </nav>
                </div>

                {/* Main Content */}
                <div className="   h-screen mb-2">

                    <main className="  h-full overflow-y-auto bg-gray-900 mx-2 rounded-md ">

                        <Routes>
                            {/* <Route path="role-manager" element={<roleManager />} /> */}
                            <Route path="user-manager" element={<UserManagement />} />
                            {/* <Route path="customer-manager" element={<customerManager />} /> */}
                            {/* <Route path="type-manager" element={<typeManager />} />
            <Route path="category-manager" element={<categoryManagement />} /> */}
                            <Route path="product-manager" element={<ProductManagement />} />
                            {/* <Route path="subscriptionplan-manager" element={<subscriptionPlanManager />} /> */}
                            <Route path="account-manager" element={<AccountManagement />} />
                            <Route path="transaction-manager" element={<transactionManager />} />
                            {/* <Route path="transactiondetail-manager" element={<transactionDetailManager />} /> */}
                            <Route path="voucher-manager" element={<VoucherManagement />} />
                            <Route path="paymentmethod-manager" element={<paymentMethodManager />} />
                            {/* <Route path="/dashboard/account-management" element={<AccountManagement />} /> */}
                            {/* <Route
                path="/"
                element={<Navigate to="user-info" replace />}
            /> */}
                        </Routes>
                    </main>
                </div>
            </div>

        </div>

    );
};

export default CMS;
