// OrderDetail.js
import React from 'react';

const OrderDetail = ({ orderId, onClose }) => {
  // Giả sử có một số chi tiết hóa đơn để hiển thị
  const orderDetails = {
    items: [
      { id: 1, name: 'Item A', price: 20.0, quantity: 2 },
      { id: 2, name: 'Item B', price: 30.5, quantity: 1 },
    ],
    total: 70.5,
    shippingAddress: '123 Main St, City, Country',
  };

  return (
    <div>
      <h2>Order Detail - #{orderId}</h2>
      <ul>
        {orderDetails.items.map(item => (
          <li key={item.id}>
            {item.name} - ${item.price} x {item.quantity}
          </li>
        ))}
      </ul>
      <p><strong>Total:</strong> ${orderDetails.total}</p>
      <p><strong>Shipping Address:</strong> {orderDetails.shippingAddress}</p>
      <button onClick={onClose} className="text-blue-500 hover:underline">Close</button>
    </div>
  );
};

export default OrderDetail;
