// roles.js
import React, { useEffect, useState } from 'react';
import transactionService from '../../services/transactionService';
import { useUser } from '../../context/userProvider';

import { Link, useNavigate } from 'react-router-dom';
import roleService from '../../services/roleService';
import Modal from 'react-modal';
import { pagination } from '../common/pagination';
import productService from '../../services/productService';
import subscriptionPlanSevice from '../../services/subscriptionPlanService';
import Alert from '../common/Alert';

import SelectCustom from '../common/SelectCustom';
import categoryService from '../../services/categoryService';
import typeService from '../../services/TypeService';
import ProductDetail from '../../pages/productDetail';
import voucherService from '../../services/voucherService';
import SelectMutipleCustom from './SelectMutipleCustom';

Modal.setAppElement('#root');
const AccountManager = () => {
    const VND = new Intl.NumberFormat('vi-VN', {
        style: 'currency',
        currency: 'VND',
    });

    const navigate = useNavigate();
    const [isModalOpen, setModalOpen] = useState(false);
    // const [page, setPage] = useState(1);
    const { user } = useUser();
    const [Vouchers, setVouchers] = useState([]);
    const openModal = () => setModalOpen(true);
    const closeModal = () => { setModalOpen(false); setModalContent(null) };
    const [ModalContent, setModalContent] = useState(null);
    const [paginationArray, setPaginationArray] = useState([]);
    const [pageNumber, setPageNumber] = useState(1);
    const [DeleteCustomer, setDeleteCustomer] = useState();


    // view add

    const AddElement = () => {
        const [Subscription_plans, setSubscriptionPlans] = useState([]);
        const [FormDataVoucher, setFormDataVoucher] = useState({
            code: "",
            discount_amount: 0,
            discount_percentage:0,
            min_order_amount: 0,
            minimize: 0,
            start_date: '',
            end_date: '',
            is_fixed_discount: false,
            quantity: 1,
            total: 100,
            categories: [],
            products: []

        })
        const [Products, setProducts] = useState([]);
        const [Categories, setCategories] = useState([]);
        
        const [Fix, setFix] = useState([{ value: '0', label: 'false' }, { value: '1', label: 'true' }]);
        const [selectedOptionStatus, setSelectedOptionFixus] = useState(null);
        const [selectedOptionType, setSelectedOptionType] = useState(null);


        const handleSubmitVoucher = async (e) => {
            e.preventDefault();
            console.log(FormDataVoucher)

            if (FormDataVoucher.roleId == 0) {
                Alert.showMessage('Please select Category ')
                return
            }

            Alert.showMessage(await voucherService.create(FormDataVoucher));
            const data = await voucherService.findAllAccountSubscriptonplanProductByPage({ page: pageNumber, size: 10 });
            setPaginationArray(pagination(data.currentPage, data.totalPages));
            // console.log(data);
            setVouchers(data);
        }
        const handleChangeFormAccount = (e) => {
            const { name, value } = e.target;
            console.log(value)
            setFormDataVoucher({
                ...FormDataVoucher,
                [name]: value,
            })
        }


        const handleSelectChangeStatus = async (newValue) => {
            // Xử lý giá trị được chọn tại đây
            setSelectedOptionStatus(newValue);
            if (newValue)
                setFormDataVoucher({
                    ...FormDataVoucher,
                    status: newValue.value,
                })

        };
        const handleSelectChangeProduct = async (inputValue) => {
            console.log('Selected Value:', inputValue);
            if (inputValue) {

                const data = (await subscriptionPlanSevice.findAll({ ProductId: inputValue.value }));
                console.log(data);
                data ? setSubscriptionPlans(data.map(item => ({
                    value: item.id,
                    label: item.packed_name,
                }))) : setSubscriptionPlans([]);
            }

        };
        const handleSelectChangeSubscriptionPlan = async (inputValue) => {
            console.log('Selected Value:', inputValue);

            setFormDataVoucher({
                ...FormDataVoucher,
                SubscriptionPlanId: inputValue ? inputValue.value : '',
            })
        };
        const handleCreateOptionStatus = async (inputValue) => {
            console.log('Selected Value:', inputValue);
            if (inputValue) {
                Alert.showMessage(await roleService.create({ name: inputValue }));
                const data = await roleService.getall();
                console.log(data);
                data ? setProducts(data.map(item => ({
                    value: item.id,
                    label: item.name,
                }))) : setProducts([]);
            }

        };
        useEffect(() => {
            const fetchData = async () => {
                try {

                    const Products = await productService.getAllProducts();
                    console.log(Products);
                    Products ? setProducts(Products.map(item => ({
                        value: item.id,
                        label: item.name,
                    }))) : setProducts([]);

                } catch (error) {
                    console.error('Error fetching :', error);
                }
            };

            fetchData();
        }, []);
        useEffect(() => {
            const fetchData = async () => {
                try {
                    console.log(FormDataVoucher)
                } catch (error) {
                    console.error('Error fetching :', error);
                }
            };

            fetchData();
        }, [FormDataVoucher]);
        return (
            <form onSubmit={handleSubmitVoucher} className='px-auto text-white w-3/4 mx-20'>
                <div className="grid grid-cols-2 gap-4 my-2">
                    <label>
                        code:
                    </label>
                    <input className='bg-gray-500 rounded-md p-2' type="text" name="information" value={FormDataVoucher?.information} onChange={handleChangeFormAccount} />
                </div>
                <div className="grid grid-cols-2 gap-4 my-2">
                    <label>
                        discount_amount:
                    </label>
                    <input placeholder='' className='bg-gray-500 rounded-md p-2' type="number" name="information" value={FormDataVoucher?.information} onChange={handleChangeFormAccount} />
                </div>
                <div className="grid grid-cols-2 gap-4 my-2">
                    <label>
                        discount_percentage:
                    </label>
                    <input className='bg-gray-500 rounded-md p-2' type="number" name="information" value={FormDataVoucher?.information} onChange={handleChangeFormAccount} />
                </div>
                <div className="grid grid-cols-2 gap-4 my-2">
                    <label>
                        min_order_amount:
                    </label>
                    <input className='bg-gray-500 rounded-md p-2' type="number" name="information" value={FormDataVoucher?.information} onChange={handleChangeFormAccount} />
                </div>
                <div className="grid grid-cols-2 gap-4 my-2">
                    <label>
                        minimize:
                    </label>
                    <input className='bg-gray-500 rounded-md p-2' type="number" name="information" value={FormDataVoucher?.information} onChange={handleChangeFormAccount} />
                </div>
                <div className="grid grid-cols-2 gap-4 my-2">
                    <label>
                        start_date:
                    </label>
                    <input className='bg-gray-500 rounded-md p-2' type="date" name="information" value={FormDataVoucher?.information} onChange={handleChangeFormAccount} />
                </div>
                <div className="grid grid-cols-2 gap-4 my-2">
                    <label>
                        end_date:
                    </label>
                    <input className='bg-gray-500 rounded-md p-2' type="date" name="information" value={FormDataVoucher?.information} onChange={handleChangeFormAccount} />
                </div>
                <div className="grid grid-cols-2 gap-4 my-2">
                    <label>
                        fix:
                    </label>
                    <SelectCustom options={Products} onChange={handleSelectChangeProduct} />

                </div>
                <div className="grid grid-cols-2 gap-4 my-2">
                    <label>
                        total:
                    </label>
                    <input className='bg-gray-500 rounded-md p-2' type="number" name="information" value={FormDataVoucher?.information} onChange={handleChangeFormAccount} />
                </div>
                <div className="grid grid-cols-2 gap-4 my-2">
                    <label>
                        Applicable to Product/Category:
                    </label>
                    <SelectCustom name='status' options={Status} onChange={handleSelectChangeStatus} />

                </div>
                <div className="grid grid-cols-2 gap-4 my-2">
                    <label>
                        products:
                    </label>
                    <SelectMutipleCustom options={Products} onChange={handleSelectChangeProduct} />

                </div>
                <div className="grid grid-cols-2 gap-4 my-2">
                    <label>
                        categories
                    </label>
                    <SelectMutipleCustom options={Subscription_plans} onChange={handleSelectChangeSubscriptionPlan} />

                </div>
                <button type="submit" className="mt-4 p-2 bg-orange-500 rounded-md my-2" >Add Account</button>
            </form>
