import React, { useEffect, useState } from 'react';
const Alert = ({ message }) => {

    toast.success(message, {
        position: 'top-right',
        autoClose: 3000, // Đóng tự động sau 3000 milliseconds (3 giây)
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
    });


};
export default Alert;
