// Orders.js
import React, { useEffect, useState } from 'react';
import transactionService from '../../services/transactionService';
import { useUser } from '../../context/userProvider';

const Orders = ({ onOrderClick }) => {
  const [page,setPage]=useState(1);
  const {user} =useUser();
  const [orders,setOrders]=useState();
  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await transactionService.findByUser({page:page,size:5,UserId:user.UserId});
        console.log(data);
        setOrders(data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchData();
  }, [page]);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await transactionService.findByUser({page:page,size:5,UserId:user.UserId});
        console.log(data);
        setOrders(data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchData();
  }, []);
  

  return (
    <div className="bg-white p-8 rounded-md shadow-md">
      <h2 className="text-2xl font-bold mb-4">Orders</h2>
      <ul className="space-y-4">
        {orders?.items?.map(order => (
          <li key={order?.items?.id} className="flex items-center justify-between border-b border-gray-300 py-2">
            <div>
              <span className="text-blue-500 font-semibold">Order #{order?.items?.id}</span>
              <p className="text-gray-600">date: {order.items.transaction_date}</p>
            </div>
            <div>
              <span className="text-green-500 font-semibold">${order.items.total_payment}</span>
              <button
                onClick={() => onOrderClick(order.id)}
                className="text-blue-500 hover:underline ml-2"
              >
                View Details
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Orders;
