import React, { useEffect, useState } from 'react';
import productService from '../services/productService';
import { useParams } from 'react-router-dom';

const ProductDetail = () => {
  const { id } = useParams();

  const [product, setProduct] = useState({
    // id,
    // name: 'Product A',
    // description: 'This is a sample product description.',
    // content: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Quisque in tincidunt velit.',
    // price: 29.99,
    // originalPrice: 39.99,
    // discount: 10,
    // image: 'https://gamikey.com/wp-content/uploads/2023/08/Banner-Kapersky.png.webp',
    // thumbnail: '',
    // published: true,
    // ProductCategoryId: 1,
    // ProductTypeId: 1,
    // sold: 50,
    // rating: 4.5,
    // similarProducts: [
    //   { id: 2, name: 'Product B', image: 'https://example.com/product-b-image.jpg' },
    //   { id: 3, name: 'Product C', image: 'https://example.com/product-c-image.jpg' },
    //   // Add more similar products
    // ],
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await productService.getProductDetail(id);
        // console.log(data);
        setProduct(data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchData();
  }, [id]);

  return (
    <div className="container mx-auto mt-10 p-6 bg-white rounded-md shadow-md">
      <div className="flex">
        <div className="w-1/2">
          <img src="https://gamikey.com/wp-content/uploads/2023/08/Banner-Kapersky.png.webp" alt={product.product.name} className="rounded-lg" />
        </div>
        <div className="w-1/2 ml-6">
          <h2 className="text-2xl font-semibold mb-4">{product.product.name}</h2>
          <div className="grid grid-cols-2 gap-4 mb-6">
            <div>
              <p className="font-semibold">Price:</p>
              {/* {product.subscriptionPlans.length>1?(<p className="text-lg font-semibold text-gray-800">${product.subscriptionPlans[0].price}-{product.subscriptionPlans[product.subscriptionPlans.length-1].price}</p>):(<p className="text-lg font-semibold text-gray-800">${product.subscriptionPlans.price}</p>)} */}
              {/* <p className="text-lg font-semibold text-gray-800">${product.subscriptionPlans.price}</p> */}
              {console.log(product)}
              {
              product.discount && (
                <p className="text-sm text-gray-500 line-through">${product.originalPrice}</p>
              )}
              {product.discount && (
                <p className="text-green-500">{`Save $${product.discount} with discount!`}</p>
              )}
            </div>
            <div>
              <p className="font-semibold">Sold:</p>
              <p className="text-gray-800">{product.sold} units</p>
            </div>
          </div>
          <div className="mb-6">
            <p className="font-semibold">Rating:</p>
            <p className="text-gray-800">{product.rating} stars</p>
          </div>
          <div className="mb-6">
            <p className="font-semibold">Description:</p>
            <p className="text-gray-700 mb-4">{product.description}</p>
          </div>
          <div className="mb-6">
            <p className="font-semibold">Content:</p>
            <p className="text-gray-700 mb-4">{product.content}</p>
          </div>
        </div>
      </div>

      {/* Similar Products Section */}
      {/* <div className="mt-10">
        <h3 className="text-xl font-semibold mb-4">Similar Products</h3>
        <div className="grid grid-cols-3 gap-6">
          {product.similarProducts.map((similarProduct) => (
            <div key={similarProduct.id} className="flex flex-col items-center">
              <img
                src={similarProduct.image}
                alt={similarProduct.name}
                className="rounded-lg mb-2"
              />
              <p className="text-sm font-semibold">{similarProduct.name}</p>
            </div>
          ))}
        </div>
      </div> */}
    </div>
  );
};

export default ProductDetail;
