// roles.js
import React, { useEffect, useState } from 'react';
import transactionService from '../../services/transactionService';
import { useUser } from '../../context/userProvider';

import { Link, useNavigate } from 'react-router-dom';
import roleService from '../../services/roleService';
import Modal from 'react-modal';
import { pagination } from '../common/pagination';
import productService from '../../services/productService';
import subscriptionPlanSevice from '../../services/subscriptionPlanService';
import Alert from '../common/Alert';
import uploads from '../../services/uploadsService';
import SelectCustom from '../common/SelectCustom';
import categoryService from '../../services/categoryService';
import typeService from '../../services/TypeService';
import ProductDetail from '../../pages/productDetail';
import customerService from '../../services/customerService';
import authService from '../../services/auth';
import accountService from '../../services/accountService';
Modal.setAppElement('#root');
const AccountManager = () => {
  const VND = new Intl.NumberFormat('vi-VN', {
    style: 'currency',
    currency: 'VND',
  });

  const navigate = useNavigate();
  const [isModalOpen, setModalOpen] = useState(false);
  // const [page, setPage] = useState(1);
  const { user } = useUser();
  const [Accounts, setAccounts] = useState([]);
  const openModal = () => setModalOpen(true);
  const closeModal = () => { setModalOpen(false); setModalContent(null) };
  const [ModalContent, setModalContent] = useState(null);
  const [paginationArray, setPaginationArray] = useState([]);
  const [pageNumber, setPageNumber] = useState(1);
  const [DeleteCustomer, setDeleteCustomer] = useState();


  // view add

  const AddElement = () => {
    const [Subscription_plans,setSubscriptionPlans]=useState([]);
    const [FormDataAccount, setFormDataAccount] = useState({
      status: '',
      SubscriptionPlanId: '',
      information: ''

    })
    const [Products, setProducts] = useState([]);
    const [types, setTypes] = useState([]);
    const [Status, setStatus] = useState([{value:1,label:0},{value:2,label:1}]);
    const [selectedOptionRole, setSelectedOptionRole] = useState(null);
    const [selectedOptionType, setSelectedOptionType] = useState(null);


    const handleSubmitAccount = async (e) => {
      e.preventDefault();
      if (FormDataAccount.roleId == 0) {
        Alert.showMessage('Please select Category ')
        return
      }

      console.log(FormDataAccount)
      Alert.showMessage(await authService.register(FormDataAccount));
      const data = await customerService.findAllCustomerUserRoleBypage({ page: pageNumber, size: 10 });
      setPaginationArray(pagination(data.currentPage, data.totalPages));
      // console.log(data);
      setAccounts(data);
    }
    const handleChangeFormAccount = (e) => {
      const { name, value } = e.target;
      console.log(value)
      setFormDataAccount({
        ...FormDataAccount,
        [name]: value,
      })
    }
   
    
    const handleSelectChangeStatus = async (newValue) => {
      // Xử lý giá trị được chọn tại đây
      setSelectedOptionRole(newValue);
      if (newValue)
        setFormDataAccount({
          ...FormDataAccount,
          status: newValue.value,
        })

    };
    const handleSelectChangeProduct = async (inputValue) => {
      console.log('Selected Value:', inputValue);
      if (inputValue) {
        
        const data = (await subscriptionPlanSevice.findAll());
        console.log(data);
        data ? setProducts(data.map(item => ({
          value: item.id,
          label: item.name,
        }))) : setProducts([]);
      }

    };
    const handleSelectChangeSubscriptionPlan = async (inputValue) => {
      console.log('Selected Value:', inputValue);
      if (inputValue) {
        
        const data = (await subscriptionPlanSevice.findAll());
        console.log(data);
        data ? setProducts(data.map(item => ({
          value: item.id,
          label: item.name,
        }))) : setProducts([]);
      }

    };
    const handleCreateOptionStatus = async (inputValue) => {
      console.log('Selected Value:', inputValue);
      if (inputValue) {
        Alert.showMessage(await roleService.create({ name: inputValue }));
        const data = await roleService.getall();
        console.log(data);
        data ? setProducts(data.map(item => ({
          value: item.id,
          label: item.name,
        }))) : setProducts([]);
      }

    };
    useEffect(() => {
      const fetchData = async () => {
        try {

          const Products = await productService.getAllProducts();
          console.log(Products);
          Products ? setProducts(Products.map(item => ({
            value: item.id,
            label: item.name,
          }))) : setProducts([]);
        



        } catch (error) {
          console.error('Error fetching :', error);
        }
      };

      fetchData();
    }, []);
    return (
      <form onSubmit={handleSubmitAccount} className='px-auto text-white w-3/4 mx-20'>
        <div className="grid grid-cols-2 gap-4 my-2">
          <label>
            information:
          </label>
          <input className='bg-gray-500 rounded-md p-2' type="text" name="information" value={FormDataAccount?.information} onChange={handleChangeFormAccount} />
        </div>
        <div className="grid grid-cols-2 gap-4 my-2">
          <label>
            Status:
          </label>
          <SelectCustom name='status' options={Status} onChange={handleSelectChangeStatus}  />
         
        </div>
        <div className="grid grid-cols-2 gap-4 my-2">
          <label>
            Product:
          </label>
          <SelectCustom  options={Products} onChange={handleCreateOptionProduct} />

        </div>
        <div className="grid grid-cols-2 gap-4 my-2">
          <label>
            Subscription plan:
          </label>
          <SelectCustom options={Products} onChange={handleSelectChangeStatus} />

        </div>
        <button type="submit" className="mt-4 p-2 bg-orange-500 rounded-md my-2" >Add Customer</button>
      </form>
    );
  };
  const EditElement = (props) => {

    const [FormDataAccount, setFormDataAccount] = useState({
      username: '',
      password: '',
      status: false,
      roleId: 0,
      full_name: '',
      address: '',
      phone_number: '',
      email_address: '',

    })
    const handleSubmitAccount = async (e) => {
      e.preventDefault();
      console.log(FormDataAccount)
      const result = await customerService.update(props?.value?.id, FormDataAccount)
      Alert.showMessage(result.message);
      const data = await customerService.findAllCustomerUserRoleBypage({ page: pageNumber, size: 10 });
      setPaginationArray(pagination(data.currentPage, data.totalPages));
      // console.log(data);
      setAccounts(data);
    }
    const handleChangeFormAccount = (e) => {
      const { name, value } = e.target;
      console.log(value)
      setFormDataAccount({
        ...FormDataAccount,
        [name]: value,
      })
    }
    const handleFileUpload = async (e) => {
      const fileInput = e.target;
      const files = fileInput.files;
      if (files.length > 0) {
        const uploadData = new FormData();
        uploadData.append("file", e.target.files[0], "file");
        const result = await uploads.uploadImg(uploadData);
        console.log(result);
        if (e.target.id == 'thumbnail')
          setFormDataAccount({
            ...FormDataAccount,
            thumbnail: result.secure_url,
          })
        else if (e.target.id == 'image')
          setFormDataAccount({
            ...FormDataAccount,
            image: result.secure_url,
          })
        // Tiếp tục xử lý formData...
      } else {
        console.error('No file selected');
      }



    }
    const [selectedOptionRole, setSelectedOptionRole] = useState(null);
    const [selectedOptionType, setSelectedOptionType] = useState(null);

    const handleSelectChangeStatus = async (newValue) => {
      // Xử lý giá trị được chọn tại đây
      setSelectedOptionRole(newValue);
      setFormDataAccount({
        ...FormDataAccount,
        roleId: newValue.value,
      })

    };
    const handleCreateOptionCustomer = async (inputValue) => {
      console.log('Selected Value:', inputValue);
      if (inputValue) {
        Alert.showMessage(await Productservice.create({ name: inputValue }));
        const data = await Productservice.getall();
        console.log(data);
        data ? setProducts(data.map(item => ({
          value: item.id,
          label: item.name,
        }))) : setProducts([]);
      }

    };

    const [Products, setProducts] = useState([]);
    const [Subscription_plans,setSubscriptionPlans]=useState([])
    useEffect(() => {
      const fetchData = async () => {
        try {
          console.log(props?.value?.id)
          const customer = await customerService.findOneCustomerUserRole(props?.value?.id);
          console.log(customer)
          setFormDataAccount({
            ...FormDataAccount,
            username: customer?.User?.username,
            password: customer?.User?.password,
            status: customer?.User?.status,
            roleId: customer?.User?.roleId,
            full_name: customer?.full_name,
            address: customer?.address,
            phone_number: customer?.phone_number,
            email_address: customer?.email_address,
          })
          // console.log(product)
          const Products = await Productservice.findAll();
          console.log(Products);
          Products ? setProducts(Products.map(item => ({
            value: item.id,
            label: item.name,
          }))) : setProducts([]);



        } catch (error) {
          console.error('Error fetching :', error);
        }
      };

      fetchData();
    }, []);
    return (
      <form onSubmit={handleSubmitAccount} className='px-auto text-white w-3/4 mx-20'>
        <div className="grid grid-cols-2 gap-4 my-2">
          <label>
            username:
          </label>
          <input className='bg-gray-500 rounded-md p-2' type="text" name="username" value={FormDataAccount?.username} onChange={handleChangeFormAccount} />
        </div>
        <div className="grid grid-cols-2 gap-4 my-2">
          <label>
            password:
          </label>
