// Header.js
import React, { useState } from 'react';
// import './tailwind.css'; // Import Tailwind CSS
import '../../assets'; // Import custom styles

const Header = () => {
  const [isMobileMenuOpen, setMobileMenuOpen] = useState(false);

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <header className="bg-gray-800 text-white p-4">
      {/* Navigation */}
      <nav className={`md:flex md:items-center ${isMobileMenuOpen ? 'block' : 'hidden'}`}>
        <a href="#" className="block mt-4 md:mt-0 md:inline-block mr-4">
          Home
        </a>
        <a href="#" className="block mt-4 md:mt-0 md:inline-block mr-4">
          About
        </a>
        <a href="#" className="block mt-4 md:mt-0 md:inline-block mr-4">
          Contact
        </a>
      </nav>

      {/* Mobile menu icon */}
      <button className="md:hidden" onClick={toggleMobileMenu}>
        <svg
          className="h-6 w-6 fill-current"
          viewBox="0 0 24 24"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path d="M4 6h16M4 12h16m-7 6h7"></path>
        </svg>
      </button>

      {/* Search */}
      <div className={`md:flex md:ml-auto mt-3 md:mt-0 ${isMobileMenuOpen ? 'block' : 'hidden'}`}>
        <input
          type="text"
          placeholder="Search"
          className="p-2 mr-2 rounded-md bg-gray-700 text-white"
        />
        <button className="p-2 bg-blue-500 rounded-md">Search</button>
      </div>

      {/* User */}
      <div className={`md:flex md:ml-auto mt-3 md:mt-0 ${isMobileMenuOpen ? 'block' : 'hidden'}`}>
        <button className="p-2 mr-2 bg-green-500 rounded-md">Log In</button>
        <button className="p-2 bg-yellow-500 rounded-md">Sign Up</button>
      </div>
    </header>
  );
};

export default Header;
