import React, { useState } from 'react';
import Modal from 'react-modal';

const VoucherModal = ({ isOpen, onClose, vouchers, onSelectVoucher }) => {
  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onClose}
      className="modal"
      overlayClassName="overlay"
    >
      <div className="max-h-screen overflow-y-scroll p-4">
        <h2 className="text-xl font-semibold mb-4">Chọn Voucher</h2>
        {vouchers.map((voucher) => (
          <div
            key={voucher.id}
            className="cursor-pointer mb-4 p-2 hover:bg-gray-100 rounded-md"
            onClick={() => onSelectVoucher(voucher)}
          >
            <h3 className="text-lg font-semibold">{`Giảm ₫${voucher.value}k`}</h3>
            <p className="text-gray-600 mb-2">{`Đơn Tối Thiểu ₫${voucher.minOrderAmount}k`}</p>
            <p className="text-gray-600">{`HSD: ${voucher.expirationDate}`}</p>
          </div>
        ))}
      </div>
    </Modal>
  );
};

const Checkout = () => {
  const [isModalOpen, setModalOpen] = useState(false);
  const [selectedVoucher, setSelectedVoucher] = useState(null);

  const openModal = () => setModalOpen(true);
  const closeModal = () => setModalOpen(false);

  const handleSelectVoucher = (voucher) => {
    setSelectedVoucher(voucher);
    closeModal();
  };

  const vouchers = [
    // ...Dữ liệu voucher
  ];

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded-md shadow-md">
      <h2 className="text-2xl font-semibold mb-6">Checkout</h2>

      {/* Section: Voucher */}
      <div className="mb-6">
        <label className="block text-gray-700 text-sm font-bold mb-2">Voucher Code</label>
        <button
          className="bg-blue-500 text-white p-2 rounded-md focus:outline-none"
          onClick={openModal}
        >
          {selectedVoucher ? `Giảm ₫${selectedVoucher.value}k` : 'Chọn Voucher'}
        </button>
        <VoucherModal
          isOpen={isModalOpen}
          onClose={closeModal}
          vouchers={vouchers}
          onSelectVoucher={handleSelectVoucher}
        />
      </div>

      {/* ... Các phần khác của trang Checkout */}
    </div>
  );
};

export default VoucherModal;
