// roles.js
import React, { useEffect, useState } from 'react';
import transactionService from '../../services/transactionService';
import { useUser } from '../../context/userProvider';

import { Link, useNavigate } from 'react-router-dom';
import roleService from '../../services/roleService';
import Modal from 'react-modal';
import { pagination } from '../common/pagination';
import productService from '../../services/productService';
Modal.setAppElement('#root');
const ProductManager = () => {


    const navigate = useNavigate();
    const [isModalOpen, setModalOpen] = useState(false);
    // const [page, setPage] = useState(1);
    const { user } = useUser();
    const [products, setProducts] = useState([]);
    const openModal = () => setModalOpen(true);
    const closeModal = () => { setModalOpen(false); setModalContent(null) };
    const [ModalContent, setModalContent] = useState(null);
    const [paginationArray, setPaginationArray] = useState([]);
    const [pageNumber, setPageNumber] = useState(1);
    // view add
    const addElement = () => {
        return (
            <span>no dung</span>
        );
    };
    // view edit
    const editElement = () => {
        return (
            <div>
                edit
            </div>
        );
    }
    const PreviewElement = () => {
        return (
            <div>
                edit
            </div>
        );
    }
    const removeElement = (item) => {
        return (
            <div>xoas </div>
        );
    }
    const SubscriptionPlanElement = (item) => {
        return (
            <div>Subscription </div>
        );
    }
    const handleClickAddElement = () => {
        openModal();
        setModalContent(addElement);
    }
    const handleClickEditElement = () => {
        openModal();
        setModalContent(editElement);
    }
    const handleClickRemove = (item) => {
        openModal();
        setModalContent(removeElement(item));
    }
    const handleClickPreview = (item) => {
        openModal();
        setModalContent(PreviewElement(item));
    }
    const handleClickSubscriptions = (item) => {
        openModal();
        setModalContent(SubscriptionPlanElement(item));
    }

    
    useEffect(() => {
        const fetchData = async () => {
            try {
                // setPaginationArray(pagination(pageNumber, ))
                const data = await productService.getAllProductsCategoryTypeByPage({ page: pageNumber, size: 10 });
                console.log(data);
                setProducts(data);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };

        fetchData();
    }, [pageNumber]);
    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await productService.getAllProductsCategoryTypeByPage({ page: pageNumber, size: 10 });
                setPaginationArray(pagination(data.currentPage, data.totalPages));
                // console.log(data);
                setProducts(data);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };

        fetchData();
    }, []);

    const handleClickPrevious = () => {
        if (pageNumber >= 2) {
            setPageNumber(pageNumber - 1);
        }
    }
    const handleClickNext = () => {
        if (pageNumber < paginationArray[paginationArray.length - 1]) {
            setPageNumber(pageNumber + 1);
        }
    }
    const handleClick = (role) => {
        navigate('/dashboard/role-details', { state: { role } });
    }



    return (
        <div class="container">
            {/* modal */}
            <Modal
                isOpen={isModalOpen}
                onRequestClose={closeModal}
                contentLabel="Example Modal"
                style={{
                    overlay: {
                        backgroundColor: 'rgba(0, 0, 0, 0.5)', // Thay đổi màu nền bên ngoài ở đây
                    },
                    content: {
                        width: '50%', // Thiết lập chiều rộng
                        height: '50%', // Thiết lập chiều cao
                        margin: 'auto',
                        background: '#080C13'
                    },
                }}

            >
                {ModalContent}
            </Modal>


            <div className='bg-gray-800 p-2'>
                {/* header */}
                <div className='my-2 mx-auto flex flex-wrap mb-4 space-x-4'>
                    <input type="text" className=' basis-2/6 rouded rounded-md px-2' placeholder='Search' />

                    <button className='basis-1/6 bg-orange-500 flex items-center justify-center rounded rounded-md px-2 py-2 text-white ' onClick={handleClickAddElement}>
                        <svg class="h-3.5 w-3.5 mr-1.5 -ml-1" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
                            <path clip-rule="evenodd" fill-rule="evenodd" d="M10 3a1 1 0 011 1v5h5a1 1 0 110 2h-5v5a1 1 0 11-2 0v-5H4a1 1 0 110-2h5V4a1 1 0 011-1z"></path>
                        </svg>
                        Add product</button>
                    <button className='basis-1/6 bg-white flex items-center px-2 py-2 justify-center rounded rounded-md'>
                        <svg xmlns="http://www.w3.org/2000/svg" aria-hidden="true" class="h-4 w-4 mr-1.5 -ml-1 text-gray-400" viewBox="0 0 20 20" fill="currentColor">
                            <path fill-rule="evenodd" d="M3 3a1 1 0 011-1h12a1 1 0 011 1v3a1 1 0 01-.293.707L12 11.414V15a1 1 0 01-.293.707l-2 2A1 1 0 018 17v-5.586L3.293 6.707A1 1 0 013 6V3z" clip-rule="evenodd"></path>
                        </svg> fillter options
                    </button>
                </div>
                {/* body */}

                <div className='overflow-x-auto bg-gray-800' >
                    <table className='w-full '>
                        <thead className='text-xs text-gray-700 uppercase bg-gray-50 dark:bg-gray-700 dark:text-gray-400 '>
                            <tr>
                                <th>Image</th>
                                <th>Name</th>
                                <th>Type</th>
                                <th>Category</th>
                                <th>Subscription plan</th>
                                <th>Rating</th>
                                <th>Total Sales</th>
                                <th>Updated Date</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        {products.items?.map(item => (
                            <tbody>
                                <tr className='border-b dark:border-gray-600 hover:bg-gray-100 dark:hover:bg-gray-700'>
                                    <td className='px-4 py-3 font-medium text-gray-900 whitespace-nowrap dark:text-white'>
                                        <img src={item?.product?.image} alt="image" srcset="https://down-vn.img.susercontent.com/file/vn-11134233-7r98o-lmnn9g0zf3dre3_tn" style={{
                                            width: '100%',
                                            height: 'auto',
                                            aspectRatio: '16 / 9',
                                            objectFit: 'cover',
                                        }} />

                                    </td>
                                    <td className='px-4 py-3 font-medium text-gray-900 whitespace-nowrap dark:text-white'>{item?.product?.name} </td>
                                    <td className='px-4 py-3 font-medium text-gray-900 whitespace-nowrap dark:text-white'>
                                        <span className='p-2 bg-blue-400 rounded-md'>
                                            {item?.product?.Product_type?.product_type_name}
                                        </span>
                                    </td>
                                    <td className='px-4 py-3 font-medium text-gray-900 whitespace-nowrap dark:text-white'>
                                        <span className='p-2 bg-yellow-600 rounded-md'> 
                                            {item?.product?.Product_category?.product_category_name}
                                        </span>
                                    </td>
                                    <td className='px-4 py-3 font-medium text-gray-900 whitespace-nowrap dark:text-white'><button  className='bg-orange-500 p-2 rounded-md'>View Details</button> </td>
                                    <td className='px-4 py-3 font-medium text-gray-900 whitespace-nowrap dark:text-white'>
                                        <div className='flex flex-row items-center space-x-4'>

                                            {item?.rating}
                                            <svg aria-hidden="true" class="w-5 h-5 text-yellow-400" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
                                                <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z"></path>
                                            </svg>
                                        </div>
                                    </td>
                                    <td className='px-4 py-3 font-medium text-gray-900 whitespace-nowrap dark:text-white'>{item?.totalSold}</td>
                                    <td className='px-4 py-3 font-medium text-gray-900 whitespace-nowrap dark:text-white'>{new Date(item?.product?.updatedAt).toLocaleString(undefined, {
                                        year: 'numeric',
                                        month: 'numeric',
                                        day: 'numeric',
                                        hour: '2-digit',
                                        minute: '2-digit',
                                        second: '2-digit',
                                    })}</td>
                                    <td className='px-4 py-3 font-medium text-gray-900 whitespace-nowrap dark:text-white '>
                                        <div className='flex flex-row items-center space-x-4'>
                                            <div>

                                                <button onClick={handleClickEditElement} id={item.product.id} type="button" data-drawer-target="drawer-update-product" data-drawer-show="drawer-update-product" aria-controls="drawer-update-product" className="py-2 px-3 flex items-center text-sm font-medium text-center text-white bg-orange-600 rounded-lg hover:bg-orange-800 focus:ring-4 focus:outline-none focus:ring-primary-300 dark:bg-primary-600 dark:hover:bg-primary-700 dark:focus:ring-primary-800">
                                                    <svg xmlns="http://www.w3.org/2000/svg" class="h-4 w-4 mr-2 -ml-0.5" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
                                                        <path d="M17.414 2.586a2 2 0 00-2.828 0L7 10.172V13h2.828l7.586-7.586a2 2 0 000-2.828z"></path>
                                                        <path fill-rule="evenodd" d="M2 6a2 2 0 012-2h4a1 1 0 010 2H4v10h10v-4a1 1 0 112 0v4a2 2 0 01-2 2H4a2 2 0 01-2-2V6z" clip-rule="evenodd"></path>
                                                    </svg>
                                                    Edit
                                                </button>
                                            </div>
                                            <div>

                                                <button onClick={() => handleClickPreview(1)} type="button" data-drawer-target="drawer-read-product-advanced" data-drawer-show="drawer-read-product-advanced" aria-controls="drawer-read-product-advanced" className="py-2 px-3 flex items-center text-sm font-medium text-center text-gray-900 focus:outline-none bg-white rounded-lg border border-gray-200 hover:bg-gray-100 hover:text-primary-700 focus:z-10 focus:ring-4 focus:ring-gray-200 dark:focus:ring-gray-700 dark:bg-gray-800 dark:text-gray-400 dark:border-gray-600 dark:hover:text-white dark:hover:bg-gray-700">
                                                    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" class="w-4 h-4 mr-2 -ml-0.5">
                                                        <path d="M12 15a3 3 0 100-6 3 3 0 000 6z"></path>
                                                        <path fill-rule="evenodd" clip-rule="evenodd" d="M1.323 11.447C2.811 6.976 7.028 3.75 12.001 3.75c4.97 0 9.185 3.223 10.675 7.69.12.362.12.752 0 1.113-1.487 4.471-5.705 7.697-10.677 7.697-4.97 0-9.186-3.223-10.675-7.69a1.762 1.762 0 010-1.113zM17.25 12a5.25 5.25 0 11-10.5 0 5.25 5.25 0 0110.5 0z"></path>
                                                    </svg>
                                                    Preview
                                                </button>
                                            </div>
                                            <div>

                                                <button onClick={() => handleClickRemove(1)} className='flex items-center text-red-700 hover:text-white border border-red-700 hover:bg-red-800 focus:ring-4 focus:outline-none focus:ring-red-300 font-medium rounded-lg text-sm px-3 py-2 text-center dark:border-red-500 dark:text-red-500 dark:hover:text-white dark:hover:bg-red-600 dark:focus:ring-red-900'>
                                                    <svg xmlns="http://www.w3.org/2000/svg" class="h-4 w-4 mr-2 -ml-0.5" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
                                                        <path fill-rule="evenodd" d="M9 2a1 1 0 00-.894.553L7.382 4H4a1 1 0 000 2v10a2 2 0 002 2h8a2 2 0 002-2V6a1 1 0 100-2h-3.382l-.724-1.447A1 1 0 0011 2H9zM7 8a1 1 0 012 0v6a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v6a1 1 0 102 0V8a1 1 0 00-1-1z" clip-rule="evenodd"></path>
                                                    </svg>
                                                    Delete</button>
                                            </div>
                                        </div>
                                    </td>
                                </tr>

                            </tbody>
                        ))}

                    </table>
                </div>

                {/* footer */}

                <nav class="flex flex-col md:flex-row justify-between items-start md:items-center space-y-3 md:space-y-0 p-4" aria-label="Table navigation">
                    <span class="text-sm font-normal text-gray-500 dark:text-gray-400">
                        Showing &nbsp;
                        <span class="font-semibold text-gray-900 dark:text-white">{products.currentPage * 10 - 9}-{(products.currentPage * 10) > products.totalItems ? products.totalItems : products.currentPage * 10} </span>
                        &nbsp; of &nbsp;
                        <span class="font-semibold text-gray-900 dark:text-white">{products.totalItems}</span>
                    </span>

                    <ul class="inline-flex items-stretch -space-x-px">
                        <li>
                            <button onClick={() => handleClickPrevious()} class="flex items-center justify-center h-full py-1.5 px-3 ml-0 text-gray-500 bg-white rounded-l-lg border border-gray-300 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white">
                                <span class="sr-only">Previous</span>
                                <svg class="w-5 h-5" aria-hidden="true" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
                                    <path fill-rule="evenodd" d="M12.707 5.293a1 1 0 010 1.414L9.414 10l3.293 3.293a1 1 0 01-1.414 1.414l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 0z" clip-rule="evenodd"></path>
                                </svg>
                            </button>
                        </li>
                        {paginationArray.map(item => (
                            <li>
                                {item == pageNumber ?
                                    (<button onClick={() => setPageNumber(item)} aria-current="page" class="flex items-center justify-center text-sm z-10 py-2 px-3 leading-tight text-primary-600 bg-primary-50 border border-primary-300 hover:bg-primary-100 hover:text-primary-700 dark:border-gray-700 dark:bg-gray-700 dark:text-white">{item}</button>) :
                                    item !== '...' ? (<button onClick={() => setPageNumber(item)} class="flex items-center justify-center text-sm py-2 px-3 leading-tight text-gray-500 bg-white border border-gray-300 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white"
                                    >{item}</button>) : (<button class="flex items-center justify-center text-sm py-2 px-3 leading-tight text-gray-500 bg-white border border-gray-300 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white"
                                    >{item}</button>)
                                }

                            </li>)
                        )}
                        {/* <li>
                            <a href="#" class="flex items-center justify-center text-sm py-2 px-3 leading-tight text-gray-500 bg-white border border-gray-300 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white">1</a>
                        </li>
                        <li>
                            <a href="#" class="flex items-center justify-center text-sm py-2 px-3 leading-tight text-gray-500 bg-white border border-gray-300 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white">2</a>
                        </li>
                        <li>
                            <a href="#" aria-current="page" class="flex items-center justify-center text-sm z-10 py-2 px-3 leading-tight text-primary-600 bg-primary-50 border border-primary-300 hover:bg-primary-100 hover:text-primary-700 dark:border-gray-700 dark:bg-gray-700 dark:text-white">3</a>
                        </li>
                        <li>
