import React, { useEffect, useState } from 'react';
import productService from '../services/productService';
import { useParams } from 'react-router-dom';

const ProductDetail = () => {
  const { id } = useParams();
    
  const [product, setProduct] = useState({
    id,
    name: 'Product A',
    description: 'This is a sample product description.',
    content: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Quisque in tincidunt velit.',
    price: 29.99,
    originalPrice: 39.99,
    discount: 10,
    image: 'https://example.com/product-image.jpg',
    thumbnail: '',
    published: true,
    ProductCategoryId: 1,
    ProductTypeId: 1,
    sold: 50,
    rating: 4.5,
    similarProducts: [
      { id: 2, name: 'Product B', image: 'https://example.com/product-b-image.jpg' },
      { id: 3, name: 'Product C', image: 'https://example.com/product-c-image.jpg' },
      // Add more similar products
    ],
    reviews: [
      { id: 1, customerName: 'John Doe', content: 'Great product!', rating: 5 },
      { id: 2, customerName: 'Jane Smith', content: 'Nice quality.', rating: 4 },
      // Add more reviews
    ],
  });

  const [activeTab, setActiveTab] = useState('details'); // 'details' or 'reviews'

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await productService.getProduct(id.id);
        setProduct(data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchData();
  }, [id]);

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  const handleAddToCart = () => {
    // Add logic to add the product to the cart
    alert('Product added to cart');
  };

  return (
    <div className="container mx-auto mt-10 p-6 bg-white rounded-md shadow-md">
      <div className="mb-6">
        <h2 className="text-2xl font-semibold mb-2">{product.name}</h2>
        <div className="grid grid-cols-2 gap-4 mb-4">
          <p className="font-semibold">Price:</p>
          <p className="text-gray-800">${product.price}</p>
          {/* Add more details here */}
        </div>
        <button
          className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition-all duration-300"
          onClick={handleAddToCart}
        >
          Add to Cart
        </button>
      </div>

      {/* Tabs */}
      <div className="mb-6">
        <div className="flex">
          <div
            className={`cursor-pointer p-4 ${activeTab === 'details' ? 'bg-blue-500 text-white' : 'text-gray-700'}`}
            onClick={() => handleTabClick('details')}
          >
            Product Details
          </div>
          <div
            className={`cursor-pointer p-4 ${activeTab === 'reviews' ? 'bg-blue-500 text-white' : 'text-gray-700'}`}
            onClick={() => handleTabClick('reviews')}
          >
            Reviews
          </div>
        </div>
      </div>

      {/* Content based on active tab */}
      {activeTab === 'details' && (
        <div>
          {/* Product details content */}
          <div className="flex">
            <div className="w-1/2">
              <img src={product.image} alt={product.name} className="rounded-lg" />
            </div>
            <div className="w-1/2 ml-6">
              <p className="text-gray-700 mb-4">{product.description}</p>
              {/* Add more product details */}
            </div>
          </div>
        </div>
      )}

      {activeTab === 'reviews' && (
        <div>
          {/* Reviews content */}
          <div className="mb-6">
            {product.reviews.map((review) => (
              <div key={review.id} className="mb-2">
                <p className="text-gray-700">{`"${review.content}" - ${review.customerName}`}</p>
                <p className="text-gray-500">{`Rating: ${review.rating} stars`}</p>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Similar Products Section */}
      <div className="mt-10">
        <h3 className="text-xl font-semibold mb-4">Similar Products</h3>
        <div className="grid grid-cols-3 gap-6">
          {/* ... (previous code) */}
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
