// /src/services/productService.js
import axios from 'axios';
const env= process.env.REACT_APP_API_URL;


const productService = {
  getProduct: async (id) => {
    try {
      
      const response = await axios.get(`${env.REACT_APP_API_URL}/product/${id}`);
      return response.data;
    } catch (error) {
      console.error('Error fetching products:', error);
      throw error;
    }
  },
  getAllProducts: async () => {
    try {
      const response = await axios.get(`${env.REACT_APP_API_URL}/product/getall`);

      return response.data;
    } catch (error) {
      console.error('Error fetching products:', error);
      throw error;
    }
  },
  getAllProductsByPage: async (data) => {
    try {
      console.log(process.env.REACT_APP_API_URL)
      const response = await axios.get(`${env.REACT_APP_API_URL}/product/findallbylbypage?page=${data.page}&size=${data.size}&categoryname=${data.categoryname}`);
      console.log(response.data);
      return response.data;
    } catch (error) {
      console.error('Error fetching products:', error);
      throw error;
    }
  },



  // ... other product-related API calls
};

export default productService;
