import React, { useState } from 'react';
import { useMediaQuery } from 'react-responsive';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const isMobile = useMediaQuery({ maxWidth: 767 });

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <header className="bg-gray-800 text-white p-4 flex justify-between items-center">
      {/* Menu */}
      <nav>
        {isMobile ? (
          // Hiển thị button menu cho màn hình mobile
          <button className="text-white hover:text-gray-300" onClick={toggleMenu}>&#9776;</button>
        ) : (
          // Hiển thị menu đầy đủ cho màn hình pc
          <ul className="flex space-x-4">
            <li><a href="#" className="hover:text-gray-300">Trang Chủ</a></li>
            <li><a href="#" className="hover:text-gray-300">Danh Mục Sản Phẩm</a></li>
            <li><a href="#" className="hover:text-gray-300">Giới Thiệu</a></li>
            <li><a href="#" className="hover:text-gray-300">Liên Hệ</a></li>
          </ul>
        )}
      </nav>

      {/* Tìm kiếm */}
      <div className="search-bar flex items-center space-x-2">
        <input type="text" placeholder="Tìm kiếm sản phẩm..." className="p-2 border border-gray-500" />
        <button type="button" className="p-2 bg-blue-500 text-white">Tìm Kiếm</button>
      </div>

      {/* Đăng nhập và Đăng kí */}
      <div className="auth-section space-x-4">
        <a href="#" className="hover:text-gray-300">Đăng Nhập</a>
        <a href="#" className="hover:text-gray-300">Đăng Kí</a>
      </div>

      {/* Hiển thị menu cho màn hình mobile khi nút menu được nhấn */}
      {isMobile && isMenuOpen && (
        <div className="mobile-menu">
          <ul>
            <li><a href="#">Trang Chủ</a></li>
            <li><a href="#">Danh Mục Sản Phẩm</a></li>
            <li><a href="#">Giới Thiệu</a></li>
            <li><a href="#">Liên Hệ</a></li>
          </ul>
        </div>
      )}
    </header>
  );
};

export default Header;
