// src/components/common/CategoryList.js

import React from 'react';

const CategoryList = () => {
  // Thêm các danh mục và sản phẩm vào mảng
  const categories = [
    { id: 1, name: 'Danh Mục 1', products: ['Sản Phẩm A', 'Sản Phẩm B', 'Sản Phẩm C'] },
    { id: 2, name: 'Danh Mục 2', products: ['Sản Phẩm A', 'Sản Phẩm B', 'Sản Phẩm C'] },
    { id: 3, name: 'Danh Mục 3', products: ['Sản Phẩm A', 'Sản Phẩm B', 'Sản Phẩm C'] },
    { id: 4, name: 'Danh Mục 3', products: ['Sản Phẩm A', 'Sản Phẩm B', 'Sản Phẩm C', 'san pham d'] },


    // ...Thêm các danh mục khác
  ];

  return (
    <div className="">
      {categories.map((category) => (
        <div key={category.id} className="mb-8">
          <h2 className="text-2xl font-bold mb-4">{category.name}</h2>
          <div className="grid grid-cols-3 gap-4">
            {category.products.map((product, index) => (
              
                <div key={index} className='rounded-md bg-gray-800'>
                  <img src="https://gamikey.com/wp-content/uploads/2023/08/Banner-Kapersky.png.webp" alt={product} className='rounded-md' />
                  <p>{product}</p>
                </div>
              

            ))}
          </div>
          <button cclassName="bg-orange-500 text-white px-4 py-2 rounded-md">Xem Thêm</button>
        </div>
      ))}
    </div>
  );
};

export default CategoryList;
