        const [FormDataVoucher, setFormDataVoucher] = useState({
            Fix: '',
            information: ''

        })
        const handleSubmitVoucher = async (e) => {
            e.preventDefault();
            console.log(FormDataVoucher)
            const result = await voucherService.update(props?.value?.id, FormDataVoucher)
            Alert.showMessage(result.message);
            const data = await voucherService.findAllAccountSubscriptonplanProductByPage({ page: pageNumber, size: 10 });
            setPaginationArray(pagination(data.currentPage, data.totalPages));
            // console.log(data);
            setVouchers(data);
        }
        // handle click
        const handleChangeFormAccount = (e) => {
            const { name, value } = e.target;
            console.log(value)
            setFormDataVoucher({
                ...FormDataVoucher,
                [name]: value,
            })
        }

        const handleSelectChangeFix = async (newValue) => {
            // Xử lý giá trị được chọn tại đây
            setSelectedOptionFix(newValue);
            setFormDataVoucher({
                ...FormDataVoucher,
                roleId: newValue.value,
            })

        };


        // ----------------------------------------------------------------------------
        useEffect(() => {
            const fetchData = async () => {
                try {
                    console.log(props.value.id)
                    const account = await voucherService.findOne({ id: props.value.id })
                    setFormDataVoucher({
                        ...FormDataVoucher,
                        information: account.information,
                        Fix: account.Fix,
                        SubscriptionPlanId: account.SubscriptionPlanId,

                    })

                } catch (error) {
                    console.error('Error fetching :', error);
                }
            };

            fetchData();
        }, []);
        return (
            <form onSubmit={handleSubmitVoucher} className='px-auto text-white w-3/4 mx-20'>
