// /src/services/api.js
const apiUrl = 'http://localhost:3000';

const api = {
    get: async (url) => {
        const response = await fetch(`${apiUrl}/${url}`);
        const data = await response.json();
        return data;
    },

    post: async (url, body) => {
        const response = await fetch(`${apiUrl}/${url}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(body),
        });
        const data = await response.json();
        return data;
    },
    // ... other HTTP methods
};

export default api;
