// Dashboard.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link, Outlet } from 'react-router-dom';
import UserInfo from '../components/features/UserInfo';
import Orders from '../components/features/Orders';
import AccountManagement from '../components/features/AccountManagement';

const Dashboard = () => {
  return (
    <div className="bg-gray-100 min-h-screen flex">
      {/* Phần bên trái */}
      <nav className="bg-gray-300 p-4 w-1/4">
        <h1 className="text-2xl font-bold mb-4">Dashboard</h1>
        <ul className="space-y-2">
          <li><Link to="/user-info" className="text-blue-500 hover:underline">User Info</Link></li>
          <li><Link to="/dashboard/orders" className="text-blue-500 hover:underline">Orders</Link></li>
          <li><Link to="/dashboard/account-management" className="text-blue-500 hover:underline">Account Management</Link></li>
        </ul>
      </nav>
      {/* Phần bên phải */}
      <div className="p-4 w-3/4">
        <UserInfo/>
      </div>
    </div>
  );
};

export default Dashboard;
