// src/components/Header.js
import React from 'react';
import logo from '../../assets/images/logo.png';

const Header = () => {
  return (
    <header className="bg-blue-900 p-4">
      <div className="container mx-auto flex justify-around items-center">
        {/* Logo hoặc Tên ứng dụng */}
        <div className="flex items-center">
          {/* <img
            src={logo}  // Thay đổi đường dẫn đến logo của bạn
            alt="Logo"
            className="h-12 w-auto mr-2"  // Điều chỉnh chiều cao và tự động điều chỉnh chiều rộng
          /> */}
          <h1 className="text-white text-2xl font-bold">AccTradeRent</h1>
        </div>

        {/* Navigation Menu */}
        <nav className="space-x-4">
          <a href="#" className="text-white hover:text-gray-300">Home</a>
          <a href="#" className="text-white hover:text-gray-300">Contact</a>
        </nav>

        {/* Tìm kiếm */}
        <div className="relative">
          <input
            type="text"
            placeholder="Search..."
            className="border-2 border-gray-300 p-2 rounded-md focus:outline-none focus:border-blue-500"
          />
          <button className="absolute right-2 top-1/2 transform -translate-y-1/2">
            {/* Thêm biểu tượng tìm kiếm ở đây (ví dụ: Font Awesome) */}
            <i className="fas fa-search text-white"></i>
          </button>
        </div>

        {/* Giỏ hàng */}
        <div className="flex items-center space-x-4">
          {/* Biểu tượng giỏ hàng hoặc liên kết đến trang giỏ hàng */}
          <a href="#" className="text-white hover:text-gray-300">
            <i className="fas fa-shopping-cart text-2xl"></i>
          </a>
          
          {/* Đăng nhập và Đăng ký */}
          <a href="#" className="text-white hover:text-gray-300">Log In</a>
          <a href="#" className="bg-white text-blue-500 px-4 py-2 rounded-md hover:bg-gray-100">Sign Up</a>
        </div>
      </div>
    </header>
  );
};

export default Header;
