// src/components/common/Header.js

import React from 'react';
// import tw from 'twin.macro'; // Import thư viện taiwindcss

// Sử dụng thư viện styled từ emotion
const HeaderContainer = tw.header`bg-gray-800 text-white p-4 flex justify-between items-center`;

const Nav = tw.nav`space-x-4`;
const SearchBar = tw.div`flex items-center space-x-2`;
const AuthSection = tw.div`space-x-4`;

const Header = () => {
  return (
    <HeaderContainer>
      {/* Menu */}
      <Nav>
        <ul tw="flex space-x-4">
          <li><a href="#" tw="hover:text-gray-300">Trang Chủ</a></li>
          <li><a href="#" tw="hover:text-gray-300">Danh Mục Sản Phẩm</a></li>
          <li><a href="#" tw="hover:text-gray-300">Giới Thiệu</a></li>
          <li><a href="#" tw="hover:text-gray-300">Liên Hệ</a></li>
        </ul>
      </Nav>

      {/* Tìm kiếm */}
      <SearchBar>
        <input type="text" placeholder="Tìm kiếm sản phẩm..." tw="p-2 border border-gray-500" />
        <button type="button" tw="p-2 bg-blue-500 text-white">Tìm Kiếm</button>
      </SearchBar>

      {/* Đăng nhập và Đăng kí */}
      <AuthSection>
        <a href="#" tw="hover:text-gray-300">Đăng Nhập</a>
        <a href="#" tw="hover:text-gray-300">Đăng Kí</a>
      </AuthSection>
    </HeaderContainer>
  );
};

export default Header;
