import React,{ useState } from "react";

const VoucherDropdown = ({ vouchers, onSelectVoucher }) => {
    const [isOpen, setIsOpen] = useState(false);
  
    const handleToggleDropdown = () => {
      setIsOpen(!isOpen);
    };
  
    const handleSelectVoucher = (voucher) => {
      setSelectedVoucher(voucher);
      onSelectVoucher(voucher); // Truyền voucher đã chọn ra ngoài
      setIsOpen(false);
    };
  
    return (
      <div className="relative inline-block">
        <button
          className="bg-blue-500 text-white p-2 rounded-md focus:outline-none"
          onClick={handleToggleDropdown}
        >
          Select Voucher
        </button>
  
        {isOpen && (
          <div className="absolute mt-2 bg-white border border-gray-300 rounded-md shadow-md">
            {vouchers.map((voucher) => (
              <div
                key={voucher.id}
                className={`p-2 cursor-pointer hover:bg-gray-100 ${
                  selectedVoucher === voucher ? 'bg-gray-100' : ''
                }`}
                onClick={() => handleSelectVoucher(voucher)}
              >
                {voucher.name}
              </div>
            ))}
          </div>
        )}
      </div>
    );
  };

  export default VoucherDropdown;
