// src/components/common/FeaturedProducts.js

import React, { useEffect, useState } from 'react';
import productService from '../../services/productService';

const FeaturedProducts = () => {
  const [products, setProducts] = useState();

  useEffect(() => {
    // Gọi hàm từ productService để lấy dữ liệu sản phẩm
    productService.getAllProducts()
      .then((data) => {
        console.log(data);
        setProducts(data);
      })
      .catch((error) => {
        console.error('Error fetching products:', error);
      });
  }, []);
  // Thêm các sản phẩm nổi bật vào mảng
  

  return (
    <div className="mt-8">
      <h2 className="text-2xl font-bold mb-4">Sản Phẩm Nổi Bật</h2>
      <div className="grid grid-cols-3 gap-4">
        {products.map((product) => (
          <div key={product.id}>
             {/* eslint-disable-next-line react/jsx-no-target-blank */}
            <img src="https://gamikey.com/wp-content/uploads/2023/08/Banner-Kapersky.png.webp" alt={product.name} />
            <p>{product.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FeaturedProducts;
