import React from 'react';
import Modal from 'react-modal';
import '../../'
const VoucherModal = ({ isOpen, onClose, vouchers, onSelectVoucher }) => {
  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onClose}
      className="modal-voucher"
      overlayClassName="overlay"
    >
      <div className="max-h-screen overflow-y-scroll p-4">
        <h2 className="text-xl font-semibold mb-4">Chọn Voucher</h2>
        {vouchers.map((voucher) => (
          <div
            key={voucher.id}
            className="cursor-pointer mb-4 p-2 hover:bg-gray-100 rounded-md"
            onClick={() => onSelectVoucher(voucher)}
          >
            <h3 className="text-lg font-semibold">{`Giảm ₫${voucher.value}k`}</h3>
            <p className="text-gray-600 mb-2">{`Đơn Tối Thiểu ₫${voucher.minOrderAmount}k`}</p>
            <p className="text-gray-600">{`HSD: ${voucher.expirationDate}`}</p>
          </div>
        ))}
      </div>
    </Modal>
  );
};

export default VoucherModal;
